//Given two strings s1 and s2, write a function to return true if s2 contains the permutation of s1. In other words, one of the first string's permutations is the substring of the second string.
//
// 
//
//Example 1:
//
//Input: s1 = "ab" s2 = "eidbaooo"
//Output: True
//Explanation: s2 contains one permutation of s1 ("ba").
//Example 2:
//
//Input:s1= "ab" s2 = "eidboaoo"
//Output: False
// 
//
//Note:
//
//The input strings only contain lower case letters.
//The length of both given strings is in range [1, 10,000].
//Accepted
//63,388
//Submissions
//160,750

bool checkInclusion(char * s1, char * s2){
    int ascii_s1[128] = {0};
    int len_s1 = (int)strlen(s1);
//    int len_s2 = strlen(s2);
    int count = len_s1;
    char *start = s2;
    char *end = s2;
    
    while (*s1) {
        ascii_s1[*s1++]++;
    }
    
//    for (int i = 0; i < 128; i++) {
//        if (!ascii_s1[i]) {
//            ascii_s1[i] = -len_s2;
//        }
//    }
    
    while (*end) {
        if (ascii_s1[*end++]-- > 0) {
            count--;
        }
//        printf("count(%d) start(%s) end(%s)\n", count, start, end);
        while (!count) {
//            printf("\n");
            if (end - start == len_s1) {
                return 1;
            }
            if (++ascii_s1[*start++] > 0) {
                count++;
            }
        }
    }
    return 0;
}


bool checkInclusion2(char * s1, char * s2){
    int ascii_s1[ASCIISIZE] = {0};
    int ascii_s2[ASCIISIZE] = {0};
    int strlen_s1 = (int) strlen(s1);
    int left = strlen_s1;
    
    char *p = s1;
    while (*p) {
        ascii_s1[*p++]++;
    }
    memcpy(ascii_s2, ascii_s1, ASCIISIZE * sizeof(int));

    for (p = s2; *p; p++) {
//        printf("p(%s)\n",p);
        if (ascii_s2[*p] > 0) {
            ascii_s2[*p]--;
            left--;
            if (left == 0) {
                return 1;
            }
        }else if(strlen_s1 == left){
        }else if(ascii_s1[*p] == 0){
            memcpy(ascii_s2, ascii_s1, ASCIISIZE * sizeof(int));
            left = strlen_s1;
        }else{
            char p2 = *p;
            while (*p != p2 || ++ascii_s2[*p] <= ascii_s1[*p]) {
                p--;
            }
            memcpy(ascii_s2, ascii_s1, ASCIISIZE * sizeof(int));
            left = strlen_s1;
        }
    }
    return 0;
}
